import React, { useEffect, useState } from 'react';
import i18n from '../../i18n';
import { Publication } from '../../services';
import { Apis } from '../../services/Api/config';
// import { Link } from 'react-router-dom'
const ThirdBannerLeft = (props) => {


    const [pub, setpub] = useState([])

    const getlist = async (type) => {
        var list = await Publication.publicationsIndex(type)
        console.log(list.data)
        if (list) { 
            let r 
            props.cat?.CategoryId? r= props.cat?.CategoryId: props.cat?.id && !props.cat?.CategoryId?r=props.cat?.id:r=null
            console.log('r',r,list.data.filter(p=>p?.categoryId==r))
            setpub(list.data.filter(p=>p?.categoryId==r))
        }

    }
    useEffect(() => {
        getlist("thirdBanner")
    }, []
    )
    return (

        <div id="shopify-section-leftbanner" className="shopify-section spaced-section">
            <div className="leftbnr">
                <div className="beffect">
                    <a href="/products" >
                        <img loading="lazy" src={Apis.pub+(pub[0]?.image)}   alt="" className="img-fluid mx-auto" width="295" height="500"/>
                    </a>
                </div>
                <div className="lefttxt">
                    <div className="lftff">
                        {/* <h5>up to 60% off</h5> */}
                        {/* <h2>fashion footwear</h2> */}
                        <a href="/products" className="btn btn-primary">{i18n.t('shop now')}
                            <svg viewBox="0 0 14 10" fill="none" aria-hidden="true" focusable="false" role="presentation" className="icon icon-arrow" >
                                <path full-rule="evenodd" clipRule="evenodd" d="M8.537.808a.5.5 0 01.817-.162l4 4a.5.5 0 010 .708l-4 4a.5.5 0 11-.708-.708L11.793 5.5H1a.5.5 0 010-1h10.793L8.646 1.354a.5.5 0 01-.109-.546z" fill="currentColor"></path>
                            </svg>
                        </a>
                    </div>
                </div>
            </div>  
        </div>      

    )

}

export default ThirdBannerLeft;
