import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { GetProducts } from "../../services";
import { Apis } from "../../services/Api/config";
import { useSelector } from "react-redux";
import ModalProductDetails from "./product-details-modal";
import Slider from "react-slick";
import { cartActions } from "../../store/shopping-cart/cartSlice";
import { wishlistActions } from "../../store/wislist/wishlistSlice";
import { useDispatch } from "react-redux";
import i18n from "../../i18n";
import CartNotification from "./../Notification";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
//import "../Categories/style.css"
const Products = () => {
  const [open, setopen] = useState("none");
  const [products, setproducts] = useState([]);
  const [product, setProduct] = useState({});
  const [show, setshow] = useState("none");
  const [productNotif, setproductNotif] = useState({});
  const [indexColors, setIndexColors] = useState(
    Array(products.length).fill(0)
  );
  const wishlist = useSelector((state) => state.wishlist.wishlistItems);
  const [index, setIndex] = useState(0);

  //console.log("wishlist",wishlist)
  //const [settings, setSettings] = useState({})
  const dispatch = useDispatch();
  const addItem = (product) => {
    setshow("block");
    setTimeout(() => {
      setshow("none");
    }, 5000);
    // show === "none" ? setshow("block") : setshow("none")
    const { id, name, image, price, reference, color } = product;
    dispatch(
      cartActions.addItem({
        id,
        name,
        image,
        price,
        reference,
        color,
      })
    );
    setproductNotif(product);
  };
  const addWishlist = (product) => {
    const { id, name, image, price, reference, color } = product;
    dispatch(
      wishlistActions.addItem({
        id,
        name,
        image,
        price,
        reference,
        color,
      })
    );
  };
  const deleteWishlist = (e) => {
    //console.log(e)
    dispatch(wishlistActions.deleteItem(e.id));
  };
  const openProduct = (product, i) => {
    setProduct(product);
    setIndexColors(i);
    open === "none" ? setopen("inline-block") : setopen("none");
  };

  const getList = async () => {
    var list1 = await GetProducts.getAllProductsGroup();

    if (list1) {
     let  list=[]
      Object.values(list1.data).map(l=>{

        Object.values(l).map(l1=>{
          list.push(l1)
        })
      })
      setIndexColors(Array(list1.data?.length).fill(0));
      setproducts(list);
    }
  };
  const settings1 = {
    //dots: false,
    //arrows: true,
    //infinite: false,
    //autoplay: false,
    className: "owl-toppro",
    //autoplaySpeed: 2000,
    //slidesToShow: 4,
    touchThreshold: 5,
    useCSS: !0,
    //rtl: false,
    accessibility: !0,
    adaptiveHeight: !1,
    arrows: !0,
    asNavFor: null,
    autoplay: !1,
    autoplaySpeed: 3e3,
    centerMode: !1,
    //centerPadding:"50px",
    cssEase: "ease",
    dots: !1,
    //dotsClass:"slick-dots",
    draggable: !0,
    easing: "linear",
    edgeFriction: 0.35,
    fade: !1,
    focusOnSelect: !1,
    focusOnChange: !1,
    infinite: !0,
    initialSlide: 0,
    lazyLoad: "ondemand",
    mobileFirst: !1,
    pauseOnHover: !0,
    pauseOnFocus: !0,
    pauseOnDotsHover: !1,
    respondTo: "window",
    //responsive:null,
    // rows:1,
