import React, { useEffect, useState,useRef } from "react";
import i18n from "../../i18n";
import { Publication } from "../../services";
import { Apis } from "../../services/Api/config";
// import { Link } from 'react-router-dom'
const Hero = () => {
  const [pub, setpub] = useState([]);
  const [activeIndex, setActiveIndex] = useState(0);
  const sliderContainerRef = useRef(null);
  const getlist = async (type) => {
    var list = await Publication.publicationsIndex(type);
    //console.log(list.data)
    if (list) {
      setpub(list.data);
    }
  };
  const handleDotClick = (index) => {
    // Mettre à jour l'index actif lorsqu'un bouton dot est cliqué
    setActiveIndex(index);

    // Déplacer le slider à la position de l'index
    const scrollX = index * window.innerWidth;
    sliderContainerRef.current.scrollLeft = scrollX;
  };

  useEffect(() => {
    getlist("hero");
    const handleScroll = () => {
      // Récupérer la position horizontale de défilement
      const scrollX = sliderContainerRef.current.scrollLeft;

      // Calculer l'index de la diapositive actuellement visible
      const currentIndex = Math.round(scrollX / window.innerWidth);

      // Mettre à jour l'index actif
      setActiveIndex(currentIndex);
    };
    // Attacher le gestionnaire d'événements au conteneur du slider
    sliderContainerRef.current.addEventListener("scroll", handleScroll);

    // Nettoyer le gestionnaire d'événements lors du démontage du composant
    return () => {
      sliderContainerRef.current.removeEventListener("scroll", handleScroll);
    };
  }, [activeIndex]);

  console.log("pub", pub);
  return (
    <section
      id="shopify-section-template--14270126194751__1641460486cee907a2"
      className="shopify-section wbslideshow"
    >
      <div
        className="s-panel"
        data-section-id="template--14270126194751__1641460486cee907a2"
      >
        <slideshow-component
          className="slider-mobile-gutter"
          role="region"
          aria-roledescription="Carousel"
          aria-label="Slideshow about our brand"
        >
          <div
            className="slideshow banner banner--adapt_image grid grid--1-col slider slider--everywhere"
            id="Slider-template--14270126194751__1641460486cee907a2"
            aria-live="polite"
            aria-atomic="true"
            data-autoplay="false"
            data-speed="5"
            ref={sliderContainerRef}
          >
            {pub.map((e, index) => {
              return (
                <div
                  className={`slideshow__slide grid__item grid--1-col slider__slide ${
                    index === activeIndex ? "active" : ""
                  }`}
                  key={index}
                  id={`Slide-template--14270126194751__1641460486cee907a2-${
                    index + 1
                  }`}
                  role="group"
                  aria-roledescription="Slide"
                  aria-label={`${index + 1} of ${pub.length}`}
                  // tabIndex="-1"
                  aria-hidden={index === activeIndex ? "true" : "false"}
                >
                  <div className="slideshow__media banner__media media">
                    <img
                      sizes="100vw"
                      src={Apis.pub + e.image}
                      loading="lazy"
                      alt=""
                      width="1470"
                      height="550"
                    />
                  </div>
                  <div className="container slideshow__text-wrapper banner__content banner__content--middle-right page-width">
                    <div className="slideshow__text banner__box  slideshow__text--center slideshow__text-mobile--center">
                      {/* <strong style={{ color: "#bababa" }} className="banner__subheading slidehsmall slidehlarge">Apple iPhone 14 Pro</strong> */}
                      <h2
                        style={{ color: "#ffffff" }}
                        className="banner__heading slidehsmall slidehlarge"
                      >
                        {e.title}
                      </h2>
                      <p style={{ color: "#ffffff" }} className="banner__text">
                        <span>{e.description}</span>
                      </p>
                      <div className="banner__buttons">
                        <a href="/products" className="button btn-primary">
                          {i18n.t("shop now")}
                        </a>
                      </div>
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
          <div className="slideshow__controls slider-buttons no-js-hidden">
            <button
              type="button"
              className="slider-button slider-button--prev"
              name="previous"
              aria-label="Previous slide"
              aria-controls="Slider-template--14270126194751__1641460486cee907a2"
            >
              <svg
                aria-hidden="true"
                focusable="false"
                role="presentation"
                className="icon icon-caret"
                viewBox="0 0 10 6"
              >
                <path
                  full-rule="evenodd"
                  clipRule="evenodd"
                  d="M9.354.646a.5.5 0 00-.708 0L5 4.293 1.354.646a.5.5 0 00-.708.708l4 4a.5.5 0 00.708 0l4-4a.5.5 0 000-.708z"
                  fill="currentColor"
                ></path>
              </svg>
            </button>
            <div className="slider-counter slider-counter--dots">
              <div className="slideshow__control-wrapper">
                {pub.map((e, index) => {
                  return (
                    <button
                      className={`slider-counter__link slider-counter__link--dots link  ${
                        index === activeIndex
                          ? "slider-counter__link--active"
                          : ""
                      }`}
                      aria-label={`Load slide ${index + 1} of ${pub.length}`}
                      aria-controls="Slider-template--14270126194751__1641460486cee907a2"
                      onClick={() => handleDotClick(index)}
                    >
                      <span className="dot"></span>
                    </button>
                    // <button className="slider-counter__link slider-counter__link--dots link" aria-label="Load slide 2 of 2" aria-controls="Slider-template--14270126194751__1641460486cee907a2">
                    //     <span className="dot"></span>
                    // </button>
                  );
                })}
              </div>
            </div>
            <button
              type="button"
              className="slider-button slider-button--next"
              name="next"
              aria-label="Next slide"
              aria-controls="Slider-template--14270126194751__1641460486cee907a2"
            >
              <svg
                aria-hidden="true"
                focusable="false"
                role="presentation"
                className="icon icon-caret"
                viewBox="0 0 10 6"
              >
                <path
                  full-rule="evenodd"
                  clipRule="evenodd"
                  d="M9.354.646a.5.5 0 00-.708 0L5 4.293 1.354.646a.5.5 0 00-.708.708l4 4a.5.5 0 00.708 0l4-4a.5.5 0 000-.708z"
                  fill="currentColor"
                ></path>
              </svg>
            </button>
          </div>

          {/* <noscript>
                        <div className="slider-buttons">
                            <div className="slider-counter">
                                <a href="#Slide-template--14270126194751__1641460486cee907a2-1" className="slider-counter__link link" aria-label="Load slide 1 of 2">
                                    1
                                </a>
                                <a href="#Slide-template--14270126194751__1641460486cee907a2-2" className="slider-counter__link link" aria-label="Load slide 2 of 2">
                                    2
                                </a>
                            </div>
                        </div>
                    </noscript> */}
        </slideshow-component>
      </div>
    </section>
  );
};

export default Hero;
