import React, { useEffect, useState } from "react";
import { Apis } from "./../../services/Api/config";
import { useDispatch } from "react-redux";
import { cartActions } from "../../store/shopping-cart/cartSlice";
import { useSelector } from "react-redux";
import CartNotification from "./../Notification";
import { GetProducts } from "../../services";
import { useLocation } from "react-router-dom";

import i18n from "../../i18n";
const DetailProductByName = (props) => {
  // const [open, setopen] = useState("block")
  const [saleproduct, setsaleproduct] = useState([]);
  const [product, setproduct] = useState([]);
  const [itemProduct, setitemProduct] = useState({});

  const [filesAll, setFilesAll] = useState([]);
  //   const [attributs, setAttributs] = useState([]);
  const [image, setImage] = useState();
  const items = useSelector((state) => state.cart.cartItems);
  const [show, setshow] = useState("none");
  const [productNotif, setproductNotif] = useState({});
  //   const history = createHistory();
  const location = useLocation();
  const parts = location.pathname.split("/");
  const lastPart = parts.pop().trim(); // Récupère le dernier élément du tableau
  const [indexColors, setIndexColors] = useState(0);

  // console.log("lastPart", itemProduct);
  // console.log("history.location.state",history.location.state)
  // const openZom = () => {

  //     open === "none" ? setopen("block") : setopen("none")
  // }
  const dispatch = useDispatch();
  const incrementItem = (product) => {
    const { id, name, image, price, reference, color } = product;
    dispatch(
      cartActions.addItem({
        id,
        name,
        image,
        price,
        reference,
        color,
      })
    );
  };
  const decreaseItem = (e) => {
    itemProduct && dispatch(cartActions.removeItem(e.id));
  };
  const addItem = (product) => {
    setshow("block");
    setTimeout(() => {
      setshow("none");
    }, 5000);
    // show === "none" ? setshow("block") : setshow("none")
    const { id, name, image, price, reference, color } = product;
    items.findIndex((e) => e.id === product?.id) === -1 &&
      dispatch(
        cartActions.addItem({
          id,
          name,
          image,
          price,
          reference,
          color,
        })
      );
    setproductNotif(product);
  };
  const BuyNow = (product) => {
    const { id, name, image, price, reference, color } = product;

    items.findIndex((e) => e.id === product?.id) === -1 &&
      dispatch(
        cartActions.addItem({
          id,
          name,
          image,
          price,
          reference,
          color,
        })
      );
    window.location = "/checkout";
  };
  const changeImage = (img) => {
    setImage(img);
  };
  const changeColor = async (index) => {
    console.log("index", index);
    setImage(product[index]?.image);
    setIndexColors(index);
    setitemProduct(items.find((e) => e.id === product[index]?.id))

    var list1 = await GetProducts.getProductByfacilated(product[index]?.id);
    //console.log("list1", list1);
    if (list1) {
      setsaleproduct(list1.data);
    }
    var list2 = await GetProducts.getGalerieProduct(product[index]?.id);
    setFilesAll(list2.data);
    // var listAP = await GetProducts.getAttributProduct(product[index]?.id);
    // if (listAP) {
    //   setAttributs(listAP.data);
    // }
  };
  const getList = async () => {
    var prod = await GetProducts.getProductByName(lastPart);
    console.log("prod.dataname", items,Object.values(prod.data)[0]);
    setproduct(Object.values(prod.data)[0]);
    setitemProduct(items.find((e) => e.id === Object.values(prod.data)[0][indexColors]?.id))

    setImage(Object.values(prod.data)[0][0]?.image);
    //setIndexColors(Array(Object.values(prod.data)[0]?.length).fill(0))
    var list1 = await GetProducts.getProductByfacilated(
      Object.values(prod.data)[0][0]?.id
    );
    //console.log("list1", list1);
    if (list1) {
      setsaleproduct(list1.data);
    }
    var list2 = await GetProducts.getGalerieProduct(
      Object.values(prod.data)[0][0]?.id
    );
    setFilesAll(list2.data);
    // var listAP = await GetProducts.getAttributProduct(Object.values(prod.data)[0][0]?.id);
    // if (listAP) {
    //   setAttributs(listAP.data);
    // }
  };
  useEffect(() => {
    getList();
    const updatedItem = items.find((e) => e.id === product[indexColors]?.id);
  setitemProduct(updatedItem);
  }, [lastPart,items,indexColors]);
  console.log("Attribut", product, product[indexColors]);

  return (
    <>
      <div class="">
        <nav class="breadcrumb row" role="navigation" aria-label="breadcrumbs">
          <div class="col-12">
            <a href="/">{i18n.t("home")}</a>

            <span aria-hidden="true">
              <i class="fa fa-angle-right"></i>
            </span>
            <span>{lastPart}</span>
          </div>
        </nav>
      </div>
      {show === "block" ? (
        <CartNotification product={productNotif} show={show} />
      ) : null}
      <section
        id="shopify-section-template--14270126424127__main"
        class="shopify-section product-section"
      >
        <div class="product product--medium product--thumbnail_slider grid grid--1-col grid--2-col-tablet">
          <div class="grid__item product__media-wrapper wbproleftimg">
            <media-gallery
              id="MediaGallery-template--14270126424127__main"
              role="region"
              class="product__media-gallery"
              aria-label="Gallery Viewer"
              data-desktop-layout="thumbnail_slider"
            >
              <div
                id="GalleryStatus-template--14270126424127__main"
                class="visually-hidden"
                role="status"
              ></div>
              <slider-component
                id="GalleryViewer-template--14270126424127__main"
                class="slider-mobile-gutter"
              >
                <a
                  class="skip-to-content-link button visually-hidden"
                  href="#ProductInfo-template--14270126424127__main"
                >
                  Skip to product information
                </a>
                <ul
                  id="Slider-Gallery-template--14270126424127__main"
                  class="product__media-list grid grid--peek list-unstyled slider slider--mobile"
                >
                  <li
                    id="proptoimg"
                    class="protopimg-item product__media-item grid__item slider__slide is-active"
                    data-media-id="template--14270126424127__main-5824634093631"
                  >
                    <noscript>
                      <div
                        class="product__media media"
                        style={{ paddingTop: "100%" }}
                      >
                        <img
                          src="./cdn.shopify.com/s/files/1/0257/0492/3199/products/38_1946x2abb.jpg?v=1575011477"
                          sizes="(min-width: 1600px) 825px, (min-width: 992px) calc(55.0vw - 10rem), (min-width: 768px) calc((100vw - 11.5rem) / 2), calc(100vw - 4rem)"
                          loading="lazy"
                          width="973"
                          height="973"
                          alt=""
                        />
                      </div>
                    </noscript>

                    <modal-opener
                      class="product__modal-opener product__modal-opener--image no-js-hidden"
