import React, { useEffect, useState } from 'react';
import i18n from '../../i18n';
import { Publication } from '../../services';
import { Apis } from '../../services/Api/config';
// import { Link } from 'react-router-dom'
const BannerCenter = () => {
    const [pub, setpub] = useState([])

    const getlist = async (type) => {
        var list = await Publication.publicationsIndex(type)
        //console.log(list.data)
        if (list) { setpub(list.data) }
    }
    useEffect(() => {
        getlist("bannerCenter")
    }, []
    )
    return (

        <div id="shopify-section-template--14270126194751__1645245062f3e2c3c9" className="shopify-section spaced-section">
            <div className="offerbnr">
                <div className="beffect">
                    <a href="/products" >
                        <img loading="lazy" src={Apis.pub + pub[0]?.image} alt="" className="img-fluid mx-auto" width="1470" height="300" />
                    </a>
                </div>
                <div className="offerbtxt">
                    <div className="ofer">
                        {/* <h4>Allrounder Shoes 2022</h4> */}
                        <h2>{pub[0]?.title}</h2>
                        <p>{pub[0]?.description}</p>
                        <a href="/products" className="btn btn-primary">{i18n.t('shop now')}</a>
                    </div>
                </div>
            </div>

        </div>

    )

}

export default BannerCenter;
