import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { Link } from "react-router-dom";
// import { Link } from 'react-router-dom'
import i18n from "../../i18n";

import { Apis } from "../../services/Api/config";
import config from "../config";
import { cartActions } from "../../store/shopping-cart/cartSlice";
import { useDispatch } from "react-redux";
import { GetUser, GetCategories, GetProducts } from "../../services";
import { useHistory } from "react-router-dom";

//import { useTranslation } from 'react-i18next';
const Header = () => {
  const [trans, setTrans] = useState("none");
  const [opencart, setOpencart] = useState("none");
  const [openKits, setopenKits] = useState("none");
  const [display, setdisplay] = useState("none");

  const [index, setIndex] = useState();
  const [search, setSearch] = useState("");
  const [indexItem, setindexItem] = useState();
  const [openItems, setopenItems] = useState("none");
  const [user, setUser] = useState({});
  const [products, setproducts] = useState([]);
  const { push } = useHistory();
  const totalq = useSelector((state) => state.cart.totalQuantity);
  const subtotal = useSelector((state) => state.cart.totalAmount);
  const items = useSelector((state) => state.cart.cartItems);
  const simulatorStorge = JSON.parse(localStorage.getItem("simulator"));
  const handleLogout = async () => {
    simulatorStorge && localStorage.removeItem("simulator");
    await GetUser.logout();
    setTimeout(function () {
      window.location.href = "/login";
    }, 1000);
  };
  // const handleChange = async (event) => {
  //     event.persist();
  //     console.log("list1", event.target.value)
  //     event.target.value ? setdisplay("block"):setdisplay("none")
  //     setSearch(event.target.value);
  //     var list1 = await GetProducts.getSearchProduct(event.target.value)

  //     if (list1) { setproducts(list1.data) }

  // };

  const handleChange = async (event) => {
    event.persist();
    // console.log("list1", event.target.value)
    event.target.value ? setdisplay("block") : setdisplay("none");
    setSearch(event.target.value);
    var list1 = await GetProducts.getSearchProduct(event.target.value);

    if (list1) {
      setproducts(Object.values(list1.data));
    }
  };

  // console.log("search", products)
  const getUser = async () => {
    let t = await GetUser.isAuthenticate();
    if (t) {
      setUser(t);
    }
  };
  const handleKeyPress = (event) => {
    if (event.key === 'Enter') {
        setdisplay("none")
        push({
        pathname: "/search",
        state: { search: search },
        });
    }
  };
  const handleSubmit = () => {

    setdisplay("none")
    push({
    pathname: "/search",
    state: { search: search },
    });
  };
  const showTranslate = () => {
    trans === "none" ? setTrans("block") : setTrans("none");
  };
  const dispatch = useDispatch();
  const incrementItem = (product) => {
    const { id, name, image, price, reference, color } = product;
    dispatch(
      cartActions.addItem({
        id,
        name,
        image,
        price,
        reference,
        color,
      })
    );
  };
  const decreaseItem = (e) => {
    dispatch(cartActions.removeItem(e.id));
  };
  const deleteItem = (e) => {
    dispatch(cartActions.deleteItem(e.id));
  };
  const Kits = (id) => {
    setIndex(id);
    index === id && openKits === "none"
      ? setopenKits("block")
      : setopenKits("none");
  };
  const ItemShow = (id) => {
    setindexItem(id);

    indexItem === id && openItems === "none"
      ? setopenItems("block")
      : setopenItems("none");
  };
  const lang = localStorage.getItem("lang");
  //const { i18n } = useTranslation();
  //console.log("i18n",i18n)
  const [selectedLang, setSelectedLang] = useState(
    lang === "ar" ? "AR" : lang === "fr" ? "FR" : lang === "en" ? "EN" : "FR"
  );
  const changeLanguage = (event) => {
    i18n.changeLanguage(event.currentTarget.dataset.id);
    localStorage.setItem("lang", event.currentTarget.dataset.id);
    setSelectedLang(localStorage.getItem("lang"));
    window.location.reload(false);
  };
  const [categories, setcategories] = useState([]);
  const [MenuMobile, setMenuMobile] = useState(false);

  const getlist = async () => {
    var list = await GetCategories.getAllCategoryList();
    //console.log(list.data)
    if (list) {
      setcategories(list.data);
    }
  };

  useEffect(() => {
    getlist();
  }, []);
  useEffect(() => {
    getUser();
  }, [trans, products]);
  //console.log("items",lang)
  return (
    <>
      {/* <div id="shopify-section-announcement-bar" class="shopify-section">
                <div class="announcement-bar" role="region" aria-label="Announcement" style={{ background: "#122745" }}>
                    <div class="container">
                        <div class="row align-items-center">
                            <div class="col-md-6 text-left">
                                {/* <svg viewBox="0 0 486.982 486.982">
                                    <path d="M131.35,422.969c14.6,14.6,38.3,14.6,52.9,0l181.1-181.1c5.2-5.2,9.2-11.4,11.8-18c18.2,5.1,35.9,7.8,51.5,7.7
                                    c38.6-0.2,51.4-17.1,55.6-27.2c4.2-10,7.2-31-19.9-58.6c-0.3-0.3-0.6-0.6-0.9-0.9c-16.8-16.8-41.2-32.3-68.9-43.8
                                    c-5.1-2.1-10.2-4-15.2-5.8v-0.3c-0.3-22.2-18.2-40.1-40.4-40.4l-108.5-1.5c-14.4-0.2-28.2,5.4-38.3,15.6l-181.2,181.1
                                    c-14.6,14.6-14.6,38.3,0,52.9L131.35,422.969z M270.95,117.869c12.1-12.1,31.7-12.1,43.8,0c7.2,7.2,10.1,17.1,8.7,26.4
                                    c11.9,8.4,26.1,16.2,41.3,22.5c5.4,2.2,10.6,4.2,15.6,5.9l-0.6-43.6c0.9,0.4,1.7,0.7,2.6,1.1c23.7,9.9,45,23.3,58.7,37
                                    c0.2,0.2,0.4,0.4,0.6,0.6c13,13.3,14.4,21.8,13.3,24.4c-3.4,8.1-39.9,15.3-95.3-7.8c-16.2-6.8-31.4-15.2-43.7-24.3
                                    c-0.4,0.5-0.9,1-1.3,1.5c-12.1,12.1-31.7,12.1-43.8,0C258.85,149.569,258.85,129.969,270.95,117.869z"></path>
                                </svg>
                                <p class="announcement-bar__message" style={{ color: "#ffffff" }}>
                                    {config.shippingL}
                                </p> 
                            </div>
                            <div class="col-md-6 text-right headtleft">


                                <localization-form class="anouncebar-lang localization-form">

                                    <div class="no-js-hidden">
                                        <div class="disclosure">
                                            <button type="button" class="disclosure__button localization-form__select localization-selector link link--text caption-large" aria-expanded="false" aria-controls="FooterLanguageList" aria-describedby="FooterLanguageLabel" style={{ color: "#ffffff" }} onClick={() => showTranslate()}>
                                                {selectedLang}
                                                <svg aria-hidden="true" focusable="false" role="presentation" class="icon icon-caret" viewBox="0 0 10 6">
                                                    <path fillRule="evenodd" clipRule="evenodd" d="M9.354.646a.5.5 0 00-.708 0L5 4.293 1.354.646a.5.5 0 00-.708.708l4 4a.5.5 0 00.708 0l4-4a.5.5 0 000-.708z" fill="currentColor"></path>
                                                </svg>

                                            </button>
                                            <ul id="FooterLanguageList" class="disclosure__list list-unstyled" style={{ display: trans }}>
                                                <li onClick={changeLanguage} data-id="en" class="disclosure__item" tabIndex="-1">
                                                    <a class="link link--text disclosure__link caption-large disclosure__link--active focus-inset" href="# " >
                                                        {i18n.t('english-label')}
                                                    </a>
                                                </li>
                                                <li onClick={changeLanguage} data-id="fr" class="disclosure__item" tabIndex="-1">
                                                    <a class="link link--text disclosure__link caption-large disclosure__link--active focus-inset" href="# ">
                                                        {i18n.t('french-label')}
                                                    </a>
                                                </li>
                                                <li class="disclosure__item" tabIndex="-1" onClick={changeLanguage} data-id="ar">
                                                    <a class="link link--text disclosure__link caption-large focus-inset" href="# ">
                                                        {i18n.t('arab-label')}
                                                    </a>
                                                </li>
                                            </ul>
                                        </div>

                                    </div>
                                </localization-form>

                                {/* <div class="wbheadwish">
                                    <div class="wishlist">

                                        <Link to="/wishlist">
                                            <svg viewBox="0 0 129 129">
                                                <path d="m121.6,40.1c-3.3-16.6-15.1-27.3-30.3-27.3-8.5,0-17.7,3.5-26.7,10.1-9.1-6.8-18.3-10.3-26.9-10.3-15.2,0-27.1,10.8-30.3,27.6-4.8,24.9 10.6,58 55.7,76 0.5,0.2 1,0.3 1.5,0.3 0.5,0 1-0.1 1.5-0.3 45-18.4 60.3-51.4 55.5-76.1zm-57,67.9c-39.6-16.4-53.3-45-49.2-66.3 2.4-12.7 11.2-21 22.3-21 7.5,0 15.9,3.6 24.3,10.5 1.5,1.2 3.6,1.2 5.1,0 8.4-6.7 16.7-10.2 24.2-10.2 11.1,0 19.8,8.1 22.3,20.7 4.1,21.1-9.5,49.6-49,66.3z"></path>
                                            </svg>
                                            <span class="wbwish">{i18n.t("wishlist")}</span>
                                            <span class="wbwishcount d-none">0</span>
                                        </Link>

                                    </div>
                                </div> 

                            </div>
                        </div>
                    </div>

                </div>
            </div> */}
      <div id="shopify-section-header" class="shopify-section">
        <svg class="hidden">
          <symbol id="icon-search" viewBox="0 0 512 512">
            <path d="M495,466.2L377.2,348.4c29.2-35.6,46.8-81.2,46.8-130.9C424,103.5,331.5,11,217.5,11C103.4,11,11,103.5,11,217.5   S103.4,424,217.5,424c49.7,0,95.2-17.5,130.8-46.7L466.1,495c8,8,20.9,8,28.9,0C503,487.1,503,474.1,495,466.2z M217.5,382.9   C126.2,382.9,52,308.7,52,217.5S126.2,52,217.5,52C308.7,52,383,126.3,383,217.5S308.7,382.9,217.5,382.9z"></path>
          </symbol>

          <symbol id="icon-close" class="icon icon-close" viewBox="0 0 18 17">
            <path
              d="M.865 15.978a.5.5 0 00.707.707l7.433-7.431 7.579 7.282a.501.501 0 00.846-.37.5.5 0 00-.153-.351L9.712 8.546l7.417-7.416a.5.5 0 10-.707-.708L8.991 7.853 1.413.573a.5.5 0 10-.693.72l7.563 7.268-7.418 7.417z"
              fill="currentColor"
            ></path>
          </symbol>
        </svg>
        <div class="header-wrapper">
          <header class="header">
            <div class="header-top">
              <div class="container">
                <div class="row menuposrow">
                  <div class="col-xl-2 col-lg-3 col-md-4 col-8 headlogo">
                    <h1 class="header__heading">
                      <Link
                        to="/"
                        className="header__heading-link link link--text focus-inset"
                      >
                        <img
                          className="img img-flui header__heading-logod"
                          src={config.image}
                          loading="lazy"
                          width="180"
                          height="90"
                          alt="eTouareg-demo"
                        />
                      </Link>
                    </h1>

                    {window.innerWidth <= 900 && (
                      <div
                        id="under-menu"
                        class="collapse"
                        style={{ display: "block" }}
                      >
                        <nav
                          id="megamenu"
                          className={
                            MenuMobile === true ? "active" : "megamenu"
                          }
                        >
                          <div
                            class="wr-menu"
                            onClick={() => setMenuMobile(true)}
                            style={{
                              display:
                                window.innerWidth <= 900 ? "block" : "none",
                            }}
                          >
                            <span>
                              <svg class="icon" viewBox="0 0 384 384">
                                <rect
                                  x="0"
                                  y="277.333"
                                  width="384"
                                  height="42.667"
                                ></rect>
                                <rect
                                  x="0"
                                  y="170.667"
                                  width="384"
                                  height="42.667"
                                ></rect>
                                <rect
                                  x="0"
                                  y="64"
                                  width="384"
                                  height="42.667"
                                ></rect>
                              </svg>
                            </span>
                          </div>

                          <ul id="wbmegalevel-1" class="level_1 menu-vertical">
                            <h2 class="wbmenuclose">
                              Menu
                              <button
                                type="button"
                                class="closebtn float-right"
                                aria-label="Menu"
                                onClick={() => {
                                  setMenuMobile(false);
                                }}
                              >
                                <svg
                                  aria-hidden="true"
                                  focusable="false"
                                  role="presentation"
                                  class="icon icon-close"
                                  fill="none"
                                  viewBox="0 0 18 17"
                                >
                                  <path
                                    d="M.865 15.978a.5.5 0 00.707.707l7.433-7.431 7.579 7.282a.501.501 0 00.846-.37.5.5 0 00-.153-.351L9.712 8.546l7.417-7.416a.5.5 0 10-.707-.708L8.991 7.853 1.413.573a.5.5 0 10-.693.72l7.563 7.268-7.418 7.417z"
                                    fill="currentColor"
                                  ></path>
                                </svg>
                              </button>
                            </h2>

                            {categories.length > 0
                              ? categories.map((cat) => {
                                  return (
                                    <li
                                      class="wbmenulink wbmenul_1 "
                                      key={cat.id}
                                    >
                                      <Link
                                        class="wbmenul1_link "
                                        to={{
                                          pathname: "/by-category",
                                          state: {
                                            cat: cat,
                                            entity: "category",
                                          },
                                        }}
                                      >
                                        {" "}
                                        {cat.name}
                                        {cat.itemskits.length > 0 && (
                                          <i
                                            className={
                                              "fa fa-angle-down float-right float-right wbmegamainhd" +
                                              (openKits === "block" &&
                                              index === cat.id
                                                ? " active"
                                                : "")
                                            }
                                            onClick={() => {
                                              Kits(cat.id);
                                            }}
                                          ></i>
                                        )}
                                      </Link>
                                      {cat.itemskits.length > 0 ? (
                                        <ul
                                          class="level_2 wbmenudropdown"
                                          style={{
                                            display:
                                              index === cat.id ? openKits : "",
                                          }}
                                        >
                                          <li class="container">
                                            <div class="row rless">
                                              {cat.itemskits.map((kits) => (
                                                <div
                                                  class="wbmegacolpart col-lg-3 col-12 cless "
                                                  key={kits.id}
                                                >
                                                  <Link
                                                    class="level_3_2_link"
                                                    to={{
                                                      pathname: "/by-category",
                                                      state: {
                                                        cat: kits,
                                                        entity: "itemskits",
                                                      },
                                                    }}
                                                  >
                                                    {kits.name}{" "}
                                                    <i
                                                      className={
                                                        "level_2__trigger wbmegamainhd" +
                                                        (openItems ===
                                                          "block" &&
                                                        indexItem === kits.id
                                                          ? " active"
                                                          : "")
                                                      }
