import React, {  useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { GetCategories } from '../../services';
import { Apis } from '../../services/Api/config';
import i18n from '../../i18n';
const Categories = () => {
    const [open, setopen] = useState("block")
    const [categories, setcategories] = useState([])
    const [MenuMobile, setMenuMobile] = useState(false)



    const openCateg = () => {

        open === "none" ? setopen("block") : setopen("none")
    }
    const getlist = async () => {
        var list = await GetCategories.getAllCategoryList()
       //console.log(list.data)
        if (list) { setcategories(list.data.filter(e=>e.status_category===1 ).sort((a, b) => new Date(a.createdAt) - new Date(b.createdAt))) }
    }

    useEffect(() => {
        getlist()
    }, []
    )

    // console.log("categories",JSON.stringify(categories.sort((a, b) => new Date(a.createdAt) - new Date(b.createdAt))))
    return (

        <div id="shopify-section-site-nav" class="shopify-section index-section">
            <div class="wbmenuup">

                <h3 class="whr-menu d-lg-block d-none" data-toggle="collapse" data-target="#under-menu" aria-label="Toggle navigation" onClick={() => openCateg()}>
                    <button class="navbar-toggler" type="button" aria-label="Center Align">
                        <div class="navbar-toggler-icon">
                            <div class="bar"></div>
                            <div class="bar"></div>
                            <div class="bar"></div>
                        </div>
                    </button>
                    <span class="cate">{i18n.t('All Categories')}</span>
                </h3>

                <div id="under-menu" class="collapse" style={{ display: open }}>
                    <nav id="megamenu" className={MenuMobile===true? "active":"megamenu"}>


                        <ul id="wbmegalevel-1" class="level_1 menu-vertical">
                            <h2 class="wbmenuclose">Menu
                                <button type="button" class="closebtn float-right" aria-label="Menu" onClick={()=>{setMenuMobile(false)}}>
                                    <svg aria-hidden="true" focusable="false" role="presentation" class="icon icon-close" fill="none" viewBox="0 0 18 17">
                                        <path d="M.865 15.978a.5.5 0 00.707.707l7.433-7.431 7.579 7.282a.501.501 0 00.846-.37.5.5 0 00-.153-.351L9.712 8.546l7.417-7.416a.5.5 0 10-.707-.708L8.991 7.853 1.413.573a.5.5 0 10-.693.72l7.563 7.268-7.418 7.417z" fill="currentColor"></path>
                                    </svg>
                                </button>
                            </h2>

                            {
                               categories.length>0 ?(categories.map((cat) =>{ return(
                                <li class="wbmenulink wbmenul_1 "key={cat.id}>
                                <Link class="wbmenul1_link "  to={{
                                        pathname: "/by-category",
                                        state: { cat:cat, entity:'category' },
                                    }} > {cat.name}
                                {cat.itemskits.filter(e=>e.status===1).length>0 && <i class="fa fa-angle-down float-right float-right wbmegamainhd" data-submenu="menu_eb14554d-8987-4409-86ef-5e3fc7019981"></i>}

                                </Link>
                             { cat.itemskits.filter(e=>e.status===1).length>0 ?(
                                    <ul class="level_2 wbmenudropdown" id="menu_eb14554d-8987-4409-86ef-5e3fc7019981">

                                            <li class="container">
                                                <div class="row rless">
                                                     { cat.itemskits.filter(e=>e.status===1).map((kits) => (
                                                        <div class="wbmegacolpart col-lg-3 col-12 cless " key={kits.id}>

                                                            <h3 style={{fontSize:"18px"}}>{kits.name} <i class="level_2__trigger wbmegamainhd" data-submenu="menu_eb14554d-8987-4409-86ef-5e3fc7019981-1"></i></h3>

                                                           { kits.items.filter(e=>e.status===1).length>0 &&
                                                             <ul class="level_3" id="menu_eb14554d-8987-4409-86ef-5e3fc7019981-1">
                                                                {kits.items.filter(e=>e.status===1).map((item) => (
                                                                    <li class="level_3__item"key={item.id}>
                                                                        <Link class="level_3__link"  to={{
                                                                            pathname: "/by-category",
                                                                            state: { cat:item, entity:'items' },
                                                                        }}>{item.name}</Link>
                                                                    </li>
                                                                )
                                                                )}



                                                             </ul>
                                                            }

                                                            <Link to={{
                                                                    pathname: "/by-category",
                                                                    state: { cat:kits, entity:'itemskits' },}}>

                                                                <img src={Apis.slug+kits.image} alt="simple sofa" class="img-fluid mx-auto wb4proimg" />
                                                            </Link>


                                                        </div>

                                                    ))
                                                    }
                                                </div>
                                            </li>

                                    </ul>
                                ):null
                                } 
                            </li>
                            )
                               }
                                )):null
                            }

                        </ul>
                        <div class="w3-overlay w3-animate-opacity" style={{ cursor: "pointer" }} id="myOverlay"></div>
                    </nav>
                </div>
            </div>
        </div>

    )

}

export default Categories;
