import React, {  useEffect, useState } from 'react';
import Header from '../../components/Header'
import Menu from "../../components/Menu";
import Credentials from '../../components/Credentials';
import Footer from "../../components/Footer";
import Categories from "../../components/Categories";
import FirstBannerLeft from "../../components/Banner/first-banner-left";
import SecondBannerLeft from "../../components/Banner/second-banner-left";
import Hero from "../../components/Hero";
import Deliveryinfo from "../../components/Deliveryinfo";
import Products from "../../components/Products";
import Adverts from "../../components/Adverts";
import TopCategories from "../../components/Categories/top-category";
import BannerCenter from "../../components/Banner/banner-center";
import { GetCategories } from '../../services';
import ThirdBannerLeft from '../../components/Banner/third-banner-left';
//import SpecialProduts from "../../components/Products/ProductSpecial/product-special";
//import { GetProducts } from "../../services";

const Homepage = () => {
  const [categories, setcategories] = useState([])
  //const [products,setproducts] = useState([])
  // const getLists = async () => {

  //     var list1 = await GetProducts.getAllProducts()
  //     // console.log("list1", list1)
  //     if (list1) { setproducts(list1.data) }
  // }
  const getlist = async () => {
      var list = await GetCategories.getTopCategory()

    //console.log('helleleoe',(list.data))
    if (list) { setcategories(list.data) }
  }  
  useEffect(() => {
    getlist()
    //getLists()
}, []

)

  return (
    <>
      <Header> </Header>
      <div className="page-container drawer-page-content" id="PageContainer">
        <Menu/>
        <main id="MainContent" className="content-for-layout focus-none" role="main" tabIndex="-1">
            <div className="container">
                <div className="row">
                    <div className="col-12 col-md-4 col-lg-2 leftw">
                        <Categories/>
                        <SecondBannerLeft cat={null}/>
                        <FirstBannerLeft cat={null}/>
                        <ThirdBannerLeft cat={null}/>

                    </div>
                    <div className="col-lg-9 col-md-12 col-12 rightw">
                        <Hero/>
                        <Deliveryinfo/>
                        <Products  />
                        {/* <SpecialProduts products={products} /> */}

                        <Adverts/>
                        <TopCategories  list={categories} />
                        <BannerCenter/>

                    </div>
                </div>
            </div>
      </main>
        <Credentials/>
        <Footer/>
      </div>
    </>
  );
};

export default Homepage;
