import React, { useEffect } from 'react';
import config from '../config';
import i18n from '../../i18n';
import { useState } from 'react';
import { Link } from 'react-router-dom';

const Menu = () => {
  const [activeMenu, setActiveMenu] = useState(false);

  const isSticky = (e) => {
    const header = document.querySelector('.cate-menu');

    const scrollTop = window.scrollY;
    //console.log("scrollTop",scrollTop)
    scrollTop >= 179 ? header.classList.add('sticky') : header.classList.remove('sticky');
  };

  useEffect(() => {

    window.addEventListener('scroll', isSticky);
    return () => {
      window.removeEventListener('scroll', isSticky);

    }
  }, [activeMenu])
  console.log(activeMenu)
  return (

    <div className="cate-menu" id='menus'>
      <div className="container">
        <div className="row">
          <div className="col-lg-3 col-md-4 col-12 hleftw d-lg-inline-block d-none">
          </div>
          <div className="col-lg-9 col-md-12 col-12 rightw">
            <div id="shopify-section-cms-menu" className="shopify-section index-section">
              <div className="row">
                <div className="col-xl-9 col-12 hmenu">
                  <div className="cms-menu">


                    <p>
                      <Link to="/home" title="" className={
                        activeMenu === "/home"
                          ? "curruent-meun"
                          : ""
                      }
                        onClick={() => {
                          setActiveMenu("/home");
                        }}
                        active={activeMenu}>{i18n.t('home')}</Link>
                    </p>
                    <p>
                      <Link to="/products"  className={
                      activeMenu === "/products"
                        ? "curruent-meun"
                        : ""
                    }
                      onClick={() => {
                        setActiveMenu("/products");
                      }}
                      active={activeMenu}>{i18n.t("Products")}</Link></p>
                    <p>
                      <Link to="/special-offers" className={
                      activeMenu === "/special-offers"
                        ? "curruent-meun"
                        : ""
                    }
                      onClick={() => {
                        setActiveMenu("/special-offers");
                      }}
                      active={activeMenu}>{i18n.t("special-offers")}</Link></p>
                    {/* <p>
                      <Link to="/blog" className={
                      activeMenu === "/blog"
                        ? "curruent-meun"
                        : ""
                    }
                      onClick={() => {
                        setActiveMenu("/blog");
                      }}
                      active={activeMenu}>{i18n.t("blog")}</Link></p> */}
                    <p>
                      <Link to="/about-us" 
                    className={
                      activeMenu === "/about-us"
                        ? "curruent-meun"
                        : ""
                    }
                      onClick={() => {
                        setActiveMenu("/about-us");
                      }}
                      active={activeMenu}>{i18n.t("about us")}</Link></p>
                    <p>
                      <Link to="/contact-us" 
                    className={
                      activeMenu === "/contact-us"
                        ? "curruent-meun"
                        : ""
                    }
                      onClick={() => {
                        setActiveMenu("/contact-us");
                      }}
                      active={activeMenu}>{i18n.t("contact us")}</Link></p>

                  </div>
                </div>
                <div className="col-lg-3 text-right offer-icon d-xl-inline-block d-none">
                  {/* <div className="special-offer">

                        <p><a href="/" title="Best Product">{config.reduction}</a></p>

                      </div> */}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>

  )

}

export default Menu;
